/*
   You are given a string, S, and a list of words, L, that are all of the same
   length. Find all starting indices of substring(s) in S that is a
   concatenation of each word in L exactly once and without any intervening
   characters.

   For example, given:
S: "barfoothefoobarman"
L: ["foo", "bar"]
You should return the indices: [0,9].
(order does not matter).
*/

#include <vector>
#include <iostream>
#include <unordered_map>
using namespace std;

class Solution {
public:
     bool isSubstring(string &S, vector<string> &L, int start, int len,
                      int maxSubLen, unordered_map<string, int>& visited) {
         if (!len) return true;
         string sub = S.substr(start, maxSubLen);
         unordered_map<string, int>::iterator it;
         it = visited.find(sub);
         if (it == visited.end() || !it->second) return false;
         it->second--;
         if (isSubstring(S, L, start+maxSubLen, len-maxSubLen, maxSubLen, visited)) {
             it->second++;
             return true;
         }
         it->second++;

         return false;
     }

     vector<int> findSubstring(string S, vector<string> &L) {
         // Find all permutations of strings in L and find substring in S
         vector<int> ret;
         if (!L.size() || !S.length()) return ret;
         int len = 0;
         int maxSubLen = L[0].length();
         unordered_map<string, int> visited;
         for (int i = 0; i < (int) L.size(); i++) {
             len += L[i].length();
             unordered_map<string, int>::iterator it = visited.find(L[i]);
             if (it == visited.end()) {
                 visited[L[i]] = 1;
             }
             else {
                 it->second++;
             }
         }
         for (int i = 0; i <= (int)S.length() - len; i++) {
             if (isSubstring(S, L, i, len, maxSubLen, visited)) {
                 ret.push_back(i);
             }
         }

         return ret;
     }
};

int main()
{
    Solution s;
    string
        S("");
    vector<string> L =
    {"dhvf","sind","ffsl","yekr","zwzq","kpeo","cila","tfty","modg","ztjg","ybty","heqg","cpwo","gdcj","lnle","sefg","vimw","bxcb"};

    vector<int> ret;
    unsigned int i;

    ret = s.findSubstring(S, L);
    for (i = 0; i < ret.size(); i++) {
        cout << ret[i] << ",";
    }
    cout << endl;


    S = "barfoothefoobarman";
    L = {"foo", "bar"};
    ret = s.findSubstring(S, L);
    for (i = 0; i < ret.size(); i++) {
        cout << ret[i] << ",";
    }
    cout << endl;

    S = "a";
    L = {"a", "a"};
    ret = s.findSubstring(S, L);
    for (i = 0; i < ret.size(); i++) {
        cout << ret[i] << ",";
    }
    cout << endl;

    S = "lingmindraboofooowingdingbarrwingmonkeypoundcake";
    L = {"fooo","barr","wing","ding","wing"};
    ret = s.findSubstring(S, L);
    for (i = 0; i < ret.size(); i++) {
        cout << ret[i] << ",";
    }
    cout << endl;

    S =
        "";
    L =
    {"ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba","ab","ba"};
    ret = s.findSubstring(S, L);
    for (i = 0; i < ret.size(); i++) {
        cout << ret[i] << ",";
    }
    cout << endl;
}
